package ProgramElements;

public class AddTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       
		int a=10;
		int b=20;
		System.out.println(addTwo(a,b));
	}
		
	public static int addTwo(int a,int b)
	{
		int sum;
		sum=a+b;
		return sum;
	}
	

}
